class Comments {  final int postId;  final int id;  final String name;  final String email;  final String body;  Comments({required this.postId, required this.id, required this.name,required this.email, required this.body});  // Factory constructor to create a Post object from JSON  factory Comments.fromJson(Map<String, dynamic> json) {    return Comments(      postId: json['postId'],      id: json['id'],      name: json['name'] ?? '', // Handle null by providing a default value      email: json['email'] ?? '',      body: json['body'] ?? '',    );  }}