class Post {  final int userId;  final int id;  final String title;  final String body;  Post({required this.userId, required this.id, required this.title, required this.body});  // Factory constructor to create a Post object from JSON  factory Post.fromJson(Map<String, dynamic> json) {    return Post(      userId: json['userId'],      id: json['id'],      title: json['title'] ?? '', // Handle null by providing a default value      body: json['body'] ?? '',    );  }}