import 'package:flutter/material.dart';import 'models/post.dart';import 'services/api_service.dart';void main() {  runApp(MyApp());}class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'Flutter API Demo',      theme: ThemeData(        primarySwatch: Colors.blue,      ),      home: PostListScreen(),    );  }}class PostListScreen extends StatefulWidget {  @override  _PostListScreenState createState() => _PostListScreenState();}class _PostListScreenState extends State<PostListScreen> {  late Future<List<Post>> futurePosts;  @override  void initState() {    super.initState();    // Initialize the futurePosts with the API call    futurePosts = ApiService().fetchPosts();  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Posts List'),      ),      body: FutureBuilder<List<Post>>(        future: futurePosts,        builder: (context, snapshot) {          if (snapshot.connectionState == ConnectionState.waiting) {            return Center(child: CircularProgressIndicator());          } else if (snapshot.hasError) {            return Center(child: Text('Error: ${snapshot.error}'));          } else if (snapshot.hasData) {            return ListView.builder(              itemCount: snapshot.data!.length,              itemBuilder: (context, index) {                final post = snapshot.data![index];                return Card(                  margin: EdgeInsets.symmetric(vertical: 8, horizontal: 16),                  child: Padding(                    padding: const EdgeInsets.all(16.0),                    child: Column(                      crossAxisAlignment: CrossAxisAlignment.start,                      children: [                        Text(                          post.title,                          style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),                        ),                        SizedBox(height: 8),                        Text(post.body),                      ],                    ),                  ),                );              },            );          } else {            return Center(child: Text('No data found.'));          }        },      ),    );  }}