import 'package:http/http.dart' as http;import 'package:http/http.dart';import 'dart:convert';import '../models/Post.dart';class ApiService {  /************ Single POST ***********/  // Fetch the Post data from the API  Future<Post> fetchPost() async {    final response = await http.get(Uri.parse('https://jsonplaceholder.typicode.com/posts/1'));    if (response.statusCode == 200) {      // If the server returns a successful response, parse the JSON      printAPILog(response);      return Post.fromJson(jsonDecode(response.body));    } else {      // If the server returns an error, throw an exception      throw Exception('Failed to load post');    }  }/************ List of POST's ***********/  // Fetch a list of Post objects from the API  Future<List<Post>> fetchPosts() async {    final response = await http.get(Uri.parse('https://jsonplaceholder.typicode.com/posts'));    if (response.statusCode == 200) {      // Parse the JSON response into a List of Post objects      List<dynamic> jsonList = jsonDecode(response.body);      return jsonList.map((json) => Post.fromJson(json)).toList();    } else {      // If the server returns an error, throw an exception      throw Exception('Failed to load posts');    }  }}void printAPILog(Response response) async{  try {    print('Response status: ${response.statusCode}');    print('Response body: ${response.body}');  } catch (error) {    print('Error: $error');  }}