import 'package:http/http.dart' as http;import 'package:http/http.dart';import 'dart:convert';import '../models/Comments.dart';class ApiServiceComments {/************ List of Comments ***********/  // Fetch a list of Comments from the API  Future<List<Comments>> fetchComments() async {    final response = await http.get(Uri.parse('https://jsonplaceholder.typicode.com/comments'));    if (response.statusCode == 200) {      printAPILog(response);      List<dynamic> jsonList = jsonDecode(response.body);      return jsonList.map((json) => Comments.fromJson(json)).toList();    } else {      // If the server returns an error, throw an exception      throw Exception('Failed to load Comments');    }  }  void printAPILog(Response response) async{    try {      print('Response status: ${response.statusCode}');      print('Response body: ${response.body}');    } catch (error) {      print('Error: $error');    }  }}