import 'dart:convert';import 'package:http/http.dart' as http;import '/models/photo.dart';class PhotoApiService {  // Fetch a list of Photo objects from the API  Future<List<Photo>> fetchPhotos() async {    final response = await http.get(Uri.parse('https://jsonplaceholder.typicode.com/photos'));    if (response.statusCode == 200) {      // Parse the JSON response into a List of Photo objects      List<dynamic> jsonList = jsonDecode(response.body);      return jsonList.map((json) => Photo.fromJson(json)).toList();    } else {      // If the server returns an error, throw an exception      throw Exception('Failed to load photos');    }  }}