import 'package:http/http.dart' as http;import 'package:http/http.dart';import 'package:project_zero/services/api_service.dart';import 'dart:convert';import '../../models/Todos.dart';class TodosApiService {  /************ List of  ***********/  // Fetch a list of Todos from the API  Future<List<Todos>> fetchTodos() async {    final response = await http.get(Uri.parse('https://jsonplaceholder.typicode.com/todos'));    if (response.statusCode == 200) {      printAPILog(response);      // Parse the JSON response into a List of Todos objects      List<dynamic> jsonList = jsonDecode(response.body);      return jsonList.map((json) => Todos.fromJson(json)).toList();    } else {      // If the server returns an error, throw an exception      throw Exception('Failed to load Comments');    }  }}