import 'package:http/http.dart' as http;import 'package:http/http.dart';import 'dart:convert';import '../models/User.dart';class UsersApiService {  /************ List of Users ***********/  // Fetch a list of Comments from the API  Future<List<User>> fetchUsers() async {    final response = await http.get(Uri.parse('https://jsonplaceholder.typicode.com/users'));    if (response.statusCode == 200) {      // Parse the JSON response into a List of Comments objects      List<dynamic> jsonList = jsonDecode(response.body);      return jsonList.map((json) => User.fromJson(json)).toList();    } else {      // If the server returns an error, throw an exception      throw Exception('Failed to load Users');    }  }  void printAPILog(Response response) async{    try {      print('Response status: ${response.statusCode}');      print('Response body: ${response.body}');    } catch (error) {      print('Error: $error');    }  }}