import 'package:flutter/material.dart';import 'package:project_zero/services/other/todos_service.dart';import 'models/Todos.dart';void main() {  runApp(MyApp());}class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'Todos App',      theme: ThemeData(        primarySwatch: Colors.blue,      ),      home: TodoListScreen(),    );  }}class TodoListScreen extends StatefulWidget {  @override  _TodoListScreenState createState() => _TodoListScreenState();}class _TodoListScreenState extends State<TodoListScreen> {  late Future<List<Todos>> futureTodos;  List<Todos> todos = [];  @override  void initState() {    super.initState();    // Initialize the futureTodos with the API call    futureTodos = TodosApiService().fetchTodos();    // Fetch the todos and store them locally for managing state    futureTodos.then((value) {      setState(() {        todos = value;      });    });  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text('Todos List'),      ),      body: todos.isEmpty          ? Center(child: CircularProgressIndicator())          : ListView.builder(        itemCount: todos.length,        itemBuilder: (context, index) {          final todo = todos[index];          return Card(            margin: EdgeInsets.symmetric(vertical: 8, horizontal: 16),            child: ListTile(              title: Text(todo.title),              leading: Checkbox(                value: todo.completed,                onChanged: (bool? value) {                  setState(() {                    // Toggle the completion state                    todos[index] = Todos(                      userId: todo.userId,                      id: todo.id,                      title: todo.title,                      completed: value ?? false,                    );                  });                },              ),            ),          );        },      ),    );  }}/******* Check Boxes are not updating... stateless UI********///// void main() {//   runApp(MyApp());// }//// class MyApp extends StatelessWidget {//   @override//   Widget build(BuildContext context) {//     return MaterialApp(//       title: 'Todos App',//       theme: ThemeData(//         primarySwatch: Colors.blue,//       ),//       home: TodoListScreen(),//     );//   }// }//// class TodoListScreen extends StatefulWidget {//   @override//   _TodoListScreenState createState() => _TodoListScreenState();// }//// class _TodoListScreenState extends State<TodoListScreen> {//   late Future<List<Todos>> futureTodos;//   List<Todos> todos = [];////   @override//   void initState() {//     super.initState();//     // Initialize the futureTodos with the API call//     futureTodos = TodosApiService().fetchTodos();//   }////   @override//   Widget build(BuildContext context) {//     return Scaffold(//       appBar: AppBar(//         title: Text('Todos List'),//       ),//       body: FutureBuilder<List<Todos>>(//         future: futureTodos,//         builder: (context, snapshot) {//           if (snapshot.connectionState == ConnectionState.waiting) {//             return Center(child: CircularProgressIndicator());//           } else if (snapshot.hasError) {//             return Center(child: Text('Error: ${snapshot.error}'));//           } else if (snapshot.hasData) {//             return ListView.builder(//               // itemCount: snapshot.data!.length,//               itemCount: snapshot.data!.length,//               itemBuilder: (context, index) {//                 final todo = snapshot.data![index];//                 return Card(//                   margin: EdgeInsets.symmetric(vertical: 8, horizontal: 16),//                   child: ListTile(//                     title: Text(todo.title),//                     leading: Checkbox(//  